import { useState } from 'react';
import logo from './logo_1.png'; 
import data from './data.js';
import { Navbar, Container, Row } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

function App() {

  let [shoes] = useState(data);
  let [ids] = useState([0, 1, 2, 3, 4, 5]);
  let [제품수, 제품수변경] = useState([0, 0, 0, 0, 0, 0]);
  let [modal, setModal] = useState(false);

  const handleAddToCart = (index) => {
    // 인덱스에 해당하는 제품 수를 1 증가시킴
    let new제품수 = [...제품수];
    new제품수[index]++;
    // 변경된 제품 수를 상태에 반영
    제품수변경(new제품수);
  };

  return (
    <div className="app">
      <Navbar className="bg-body-tertiary">
        <Container>
          <Navbar.Brand className="brand ibm-plex-sans-kr-bold" href="#home">
            <img
              alt=""
              src={logo}
              width="40"
              height="40"
              className="d-inline-block align-top" 
            />{' '}
            주문하기
          </Navbar.Brand>
        </Container>
      </Navbar>

      <div className="content">

        <div className="blank1">공백</div>

        <div className="menu">메뉴
          <Container>
            <Row Row md={3}>
                {shoes.slice(0, 3).map((shoe, i) => (
                  <Card key={ids[i]} shoes={shoe} index={i} onAddToCart={handleAddToCart} count={제품수[i]} />
                ))} 
            </Row>
          </Container>
          <Container>
            <Row Row md={3}>
                {shoes.slice(3, 6).map((shoe, i) => (
                  <Card key={ids[i + 2]} shoes={shoe} index={i} onAddToCart={handleAddToCart} count={제품수[i]} />
                ))} 
            </Row>
          </Container>
        </div>

        <div className="cart ibm-plex-sans-kr-semibold">장바구니
        {

        }

        </div>
      </div>
      <div className="navbar">button</div>
    </div>
  );
}

export default App;

function Card({ shoes, index, onAddToCart }) {
  // 담기 버튼 클릭 시 제품 수 증가 함수 호출
  const handleClick = () => {
    onAddToCart(index);
  };

  return (
    <div className="col-md-4">
      <img src="https://codingapple1.github.io/shop/shoes1.jpg" alt={shoes.title} width="80%" />
      <h4>{shoes.title}</h4>
      <p>{shoes.price}</p>
      <button onClick={handleClick}> 담기 </button>
    </div>
  );
}

