import { useState } from 'react';
import logo from './logo.svg';
import data from './data.js';
import { Navbar, Nav, Container, Row } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

function App() {

  let [shoes] = useState(data);
  let [ids] = useState([0, 1, 2, 3, 4, 5]);
  let [제품수, 제품수변경] = useState([0, 0, 0, 0, 0, 0]);

  const handleAddToCart = (index) => {
    // 인덱스에 해당하는 제품 수를 1 증가시킴
    let new제품수 = [...제품수];
    new제품수[index]++;
    // 변경된 제품 수를 상태에 반영
    제품수변경(new제품수);
  };

  return (
    <div className="app">
      <Navbar bg="dark" variant="dark">
        <Container>
        <Navbar.Brand href="#home">Navbar</Navbar.Brand>
        <Nav className="me-auto">
          <Nav.Link href="#home">Home</Nav.Link>
          <Nav.Link href="#features">Features</Nav.Link>
          <Nav.Link href="#pricing">Pricing</Nav.Link>
        </Nav>
        </Container>
      </Navbar>
      <div className="content">
        <div className="blank1"> </div>
        <div className="menu">메뉴
        <Container>
          <Row Row md={3}>
              {shoes.slice(0, 3).map((shoe, i) => (
                <Card key={ids[i]} shoes={shoe} index={i} onAddToCart={handleAddToCart} count={제품수[i]} />
              ))} 
          </Row>
        </Container>
        <Container>
          <Row Row md={3}>
              {shoes.slice(3, 6).map((shoe, i) => (
                <Card key={ids[i + 2]} shoes={shoe} index={i} onAddToCart={handleAddToCart} count={제품수[i]} />
              ))} 
          </Row>
        </Container>
        </div>
        <div className="cart">장바구니</div>
      </div>
      <div className="navbar">button</div>
    </div>
  );
}

export default App;

function Card({ shoes, index, onAddToCart }) {
  // 담기 버튼 클릭 시 제품 수 증가 함수 호출
  const handleClick = () => {
    onAddToCart(index);
  };

  return (
    <div className="col-md-4">
      <img src="https://codingapple1.github.io/shop/shoes1.jpg" alt={shoes.title} width="80%" />
      <h4>{shoes.title}</h4>
      <p>{shoes.price}</p>
      <button className="cart" onClick={handleClick}> 담기 </button>
    </div>
  );
}
